import style from './Form.module.css'
import {useState} from 'react'
import validate from '../../validation';
/*const validate = (form, setErrors, errors) =>{
    
    if(!form.email) setErrors({...errors, email: "Email Vacio"});
    else{    
        if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
        setErrors({...errors, email: "ok"})
        else setErrors({...errors, email : "Email Invalido"});
    }
    if(!form.password) setErrors({...errors, password: "Pasword Vacio"});
    else{
        if (/^(?=.*\d).{6,10}$/.test(form.password)) 
        setErrors({...errors, password: "ok"})
        else setErrors({...errors, password : "Password Invalido"})
    }
}*/
const Form = () => {

    const [form, setForm] = useState({
        email: "",
        password: "",
    });

    const [errors, setErrors] = useState({
        email: "",
        password: "",
    });

    const handleChange = (event) => {
       const property = event.target.name
       const value = event.target.value

       setForm({...form, [property]: value});

       validate({...form, [property]: value}, setErrors, errors)
    }
    
    const submitHandler = (event) => {
        event.preventDefault();
        alert("Login Exitoso")
    }

    return (
         <div className={style.formulario}>
            <div className={style.title}>Welcome</div>
            <div className={style.title}>to</div>
            <div className={style.title}>Rick and Morty</div>
            <div className={style.title}>Cards Page</div>
         <form className={errors.email === "ok" && errors.password === "ok"? style.containerOk : style.containerNot} onSubmit={submitHandler}>
                <div className={errors.email === "ok"? style.emailOk : style.emailNot}>
                    <label htmlFor="email"> Email </label>
                    <input
                    type="text" 
                    name="email" 
                    value={form.email}
                    placeholder="Insert a Valid Email"
                    onChange={handleChange}   
                    />
                </div>
                <div className={errors.password === "ok"? style.passOk : style.passNot}>
                    <label htmlFor="password"> Pass </label>
                    <input 
                    type="password"
                    name="password"
                    placeholder="6-10 char & least 1 number"
                    value={form.password}
                    onChange={handleChange}
                    />
                </div>
                <div className={style.login}>
                    <button className={errors.email === "ok" && errors.password === "ok"? style.btnOk : style.btnNot} type="submit" focuse>Login</button>
                </div>
            </form>
            </div>
    )
}
export default Form
