import style from './Form.module.css'
import {useState} from 'react'
const validateEmail = (form, setErrors, errors) =>{
    
    if(!form.email) setErrors({...errors, email: "Email Vacio"});
    else{    
        if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
        setErrors({...errors, email: ""})
        else setErrors({...errors, email: "Email Invalido"});
    }
    
    
}
convalidatePassword = (form, setErrors, errors) => {
    if(!form.password) setErrors({...errors, password: "Pasword Vacio"});
    else{
        if (/^(?=.*\d).{6,10}$/.test(form.password)) 
        setErrors({...errors, password: ""})
        else setErrors({...errors, password : "Password Invalido"})
    }
}
const Form = () => {

    const [form, setForm] = useState({
        email: "",
        password: "",
    });

    const [errors, setErrors] = useState({
        email: ".",
        password: ".",
    });

    const handleChange = (event) => {
       const property = event.target.name
       const value = event.target.value

       setForm({...form, [property]: value});

       validateEmail({...form, [property]: value}, setErrors, errors)
       validatePassword({...form, [property]: value}, setErrors, errors)
    }
    
    const submitHandler = (event) => {
        event.preventDefault();
        alert("Login Exitoso")
    }

    return (
         <div className={style.formulario}>
            <div className={style.title}>Welcome</div>
            <div className={style.title}>to</div>
            <div className={style.title}>Rick and Morty</div>
            <div className={style.title}>Cards Page</div>
         <form className={errors.email === ""&&errors.password === ""? style.containerOk : style.containerNot} onSubmit={submitHandler}>
                <div className={errors.email === ""? style.userNameOk : style.userNameNot}>
                    <label htmlFor="email"> Email </label>
                    <input
                    type="text" 
                    name="email" 
                    value={form.email}
                    onChange={handleChange}    
                    />
                </div>
                <div className={errors.password === ""? style.passOk : style.passNot}>
                    <label htmlFor="password"> Pass </label>
                    <input 
                    type="text"
                    name="password"
                    value={form.password}
                    onChange={handleChange}
                    />
                </div>
                <div className={style.login}>
                    <button className={errors.email === ""&&errors.password === ""? style.btnOk : style.btnNot} type="submit">Login</button>
                </div>
            </form>
            </div>
    )
}
export default Form
