import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import styles from './App.module.css'
import {useState} from 'react';
import axios from 'axios';

function App() {
   const [characters, setCharacters] = useState([]);
   const onSearch = (id) =>{
      if(!!characters.find((character) => character.id == id))
      return window.alert("Esa Carta ya fue Agregada")
      .catch((error) => {
         window.alert("No existe un pesonaje con ese ID, solo son 826 personajes")
      })
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
         if (data.name) {
            setCharacters((characters) => [...characters, data]);
         } else {
         window.alert('¡No hay personajes con este ID!');
      }
   });
}
const onClose = (id) => {
      setCharacters(characters.filter((characters) => characters.id !== id))
   }
   return (
      <div className={styles.app}>
         <Nav onSearch = {onSearch}></Nav>
         <Cards characters={characters} onClose={onClose}/>
      </div>
   );
}
export default App;
