const validate = (form, setErrors, errors) =>{
    
    if(!form.email) setErrors({...errors, email: "EmEmail"});
    else{    
        if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email))
        setErrors({...errors, email: "ok"})
        else setErrors({...errors, email : "Email Invalido"});
    }
    if(!form.password) setErrors({...errors, password: "Password Vacio"});
    else{
        if (/^(?=.*\d).{6,10}$/.test(form.password)) 
        setErrors({...errors, password: "ok"})
        else setErrors({...errors, password : "Password Invalido"})
    }
}
export default validate;
