import { REMOVE_FAV, ADD_FAV } from "./actions-type"
const initialState = {
    myFavourites: []
}
function rootReducer (state = initialState, action){
    switch (action.type){
        case ADD_FAV:
            return{
                ...state
            }
        default:
            return state
        
        
    }
}

export default rootReducer
