const validate = (form, setErrors, errors) =>{

    if(!form.password){
        setErrors({...errors, password: "Empty Password"});
    }else{
        if (/^(?=.*\d).{6,10}$/.test(form.password)){ 
            setErrors({...errors, password: "ok"})
        }else{
            setErrors({...errors, password : "Invalid Password"})
        }
    }
    
    if(!form.email){
        setErrors({...errors, email: "Empty 2 Email"})
    }else{    
        if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(form.email)){
            setErrors({...errors, email: "ok"})
        } else {
            setErrors({...errors, email : "Invalid Email"});
        }
    }
}
export default validate;
