import style from "./Card.module.css";
import { Link } from 'react-router-dom'
import { useState } from "react";
import { addFav, removeFav} from '../../Redux/actions'
import { connect } from 'react-redux'
function Card({onClose, name, image, id}) {
   
   const [isFav, setIsFav] = useState(false)

   const handleFavourite = () => {
      if(isFav){
         setIsFav(false)
         removeFav(id)
      }else {
         setIsFav(true)
         addFav(id)
      }
   }
   
   return (
      <div className={style.container} style={{ backgroundImage: `url(${image})` }}>
         <button className={style.favBtn}>‚ù§Ô∏è</button>
         {
      isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
   ) : (
      <button onClick={handleFavorite}>ü§ç</button>
   )
}
         <button className={style.button} onClick={()=>onClose(id)}>X</button>
         <div className={style.name}>{name}</div>
         <div className={style.Details}>
         <Link to = {`/detail/${id}`}><button className={style.btnDet}>Details</button></Link>
         </div>
      </div>
   );
}
export function mapDispatchToProps (dispatch){
   return{
      addFav: function (character){
         dispatch(addFav(character))
      },
      removeFav: function(id){
         dispatch(removeFav(id))
      }
   }
}
export default connect (null, mapDispatchToProps)(Card)
